// A component that displays a single tea item
import { Col, Row } from 'antd';
import bakeryData from "/Users/stephanie/Desktop/CS Code 2022/CS1300 UIUX/DevelopmentUIUX/src/assets/bakery-data.json";

function TeaItem(props) {
    return (
        <div class="grid-item">
            <h2>Name: {props.item.name}</h2>
            <br></br>
            <img src={props.item.image} alt="tea item"></img>
            <br></br>
            <p>{props.item.description}.</p>
            <h3>Price: ${props.item.price}</h3>
            <button onClick={() => props.addToCart(props.item)}>Add to Cart</button>
            <br></br>
        </div>
    )
}

export default TeaItem;
