import './App.css';
import { useState } from "react";
import bakeryData from "./assets/bakery-data.json";
import BakeryItem from "./components/BakeryItem.js";
import TeaItem from './components/TeaItem';

function App() {
  const [cartItems, setCartItems] = useState([])

  function addToCart(item)  {
    setCartItems([...cartItems, item])
  }

  function calculateTotal()  {
    let total = 0;
    for(let i = 0; i < cartItems.length; i++) {
        total += cartItems[i].price
    }
    return total;
  }

  return (
    <div className="App">
      <h1>My Tea Shop</h1> {/* TODO: personalize your bakery (if you want) */}
      <TeaItem />
      <div className="block teaItems">
            <h2>Tea Items</h2>
            <Row gutter = {24}>
                {
                bakeryData.map(item => {
                    return(
                        <Col lg = {4}>
                        <div className = "content">
                            <div className = "image">
                            <img src={item.image} alt={item.name} />
                            </div>
                        <h3>{item.name}</h3>
                        <p>{item.description}</p>
                        <h3>Price: ${item.price}</h3>
                        </div>
                        </Col>
                    )
                })
                }

            </Row>
        </div>

      {bakeryData.map((item, index) => ( // TODO: map bakeryData to BakeryItem components
      <div>
        <BakeryItem item = {item} addToCart={addToCart}></BakeryItem>
        <br></br>
      </div>

      ))}

      <div>
        <h2>Cart</h2>
        {/* TODO: render a list of items in the cart */}
        {cartItems.map((item, index) => (<p>{item.name}<br></br>{item.price}</p>))}
        <h3>Total:</h3>
        {calculateTotal()}


      </div>
    </div>
  );
}

export default App;
