import './MainItems.css';
import { useState } from "react";
import bakeryData from "/Users/stephanie/Desktop/CS Code 2022/CS1300 UIUX/DevelopmentUIUX/src/assets/tea-data.json";
import TeaItem from '/Users/stephanie/Desktop/CS Code 2022/CS1300 UIUX/DevelopmentUIUX/src/components/TeaItem.js';
import { Col, Row } from 'antd';

// import background from "./assets/images/DevelopmentBackground.png";

function MainItems() {
  const [cartItems, setCartItems] = useState([])
  const [renderList, setRenderList] = useState(bakeryData.sort((a, b) => a.name > b.name ? 1 : -1))

  function addToCart(item) {
    // if this item is not in cart yet
    if (cartItems.every(cartItem => cartItem.name !== item.name )) {
    // add it to cart
      item.quantity = 1
      setCartItems([...cartItems, item])
    } else {
      // increase its quantity
      item.quantity += 1
      setCartItems(cartItems =>
        cartItems.filter(cartItems => cartItems.name !== "not tea")
      );
    }
  }

  function calculateTotal() {
    let total = 0;
    for (let i = 0; i < cartItems.length; i++) {
      total += cartItems[i].price * cartItems[i].quantity
    }
    return (total).toFixed(2);
  }

  function removeFromCart(item) {
    if (item.quantity != 1) {
      item.quantity -= 1
      setCartItems(cartItems =>
        cartItems.filter(cartItems => cartItems.name !== "not tea")
      );
    } else {
      setCartItems(cartItems =>
        cartItems.filter(cartItems => cartItems.name !== item.name)
      );
    }
  }


  function filterRender(whichFilter, tag) {
    if (whichFilter === "type") {
      setRenderList(renderList =>
        // filter to only include items whose tag matches the filter
        renderList.filter(listItem => listItem.type === tag)
      )
    } else if (whichFilter === "caffeine") {
      setRenderList(renderList =>
        // filter to only include items whose tag matches the filter
        renderList.filter(listItem => listItem.caffeine === tag)
      )
    }
  }

  function sortList(parameter) {
    if (parameter === "cheapest") {
      var compare = (a, b) => a.price - b.price
    } else if (parameter === "priciest") {
      var compare = (a, b) => b.price - a.price
    } else if (parameter === "abc") {
      var compare = (a, b) => a.name > b.name ? 1 : -1;
    }
    setRenderList(renderList =>
      // sort by parameter
      renderList.sort(compare)
    )
    setCartItems(cartItems =>
      cartItems.filter(cartItems => cartItems.name !== "not tea")
    )
  }

  // Erase all filters and sort alphabetically
  function listReset() {
    var compare = (a, b) => a.name > b.name ? 1 : -1;
    setRenderList(bakeryData.sort(compare))
    setCartItems(cartItems =>
      cartItems.filter(cartItems => cartItems.name !== "not tea")
    )
  }

  return (
    <div className="MainItems">
      <div>
      <h1>My Tea Shop</h1>

      <div class = "row"  className="toolbar">
        <div class = "row">
        <h2>Filters</h2>
        <button onClick={() => listReset()}>reset</button>
        </div>


        <div class = "column">
        <h3>Drink Type</h3>
        <button onClick={() => filterRender("type", "tea")}>tea</button>
        <button onClick={() => filterRender("type", "coffee")}>coffee</button>
        <button onClick={() => filterRender("type", "juice")}>juice</button>
        <button onClick={() => filterRender("type", "milk")}>milk</button>
        </div>

        <div class = "column">
        <h3>Caffienated</h3>
        <button onClick={() => filterRender("caffeine", "yes")}>yes</button>
        <button onClick={() => filterRender("caffeine", "no")}>no</button>
        </div>

        <div class = "column">
        <h2>Sort by</h2>
        <button onClick={() => sortList("abc")}>alphabetically</button>
        <button onClick={() => sortList("cheapest")}>price low to high</button>
        <button onClick={() => sortList("priciest")}>price high to low</button>
        </div>


      </div>



      <div class="row">

        <div class="column">
          {/* Map tea items from info page to grid */}
          <Row gutter={16}>
            {renderList.map((item, index) => (
              <Col offset={4} lg={4}>
                <div>
                  <TeaItem item={item} addToCart={addToCart}></TeaItem>
                  <br></br>
                </div>
              </Col>
            ))}
          </Row>
        </div>

        <div class="column">
          {/* Render a list of items in the cart */}
          <div class="cart-card">
            <h2>Cart</h2>
            {cartItems.map((item, index) => (
              <div>
              <p>{item.quantity}x {item.name}<br></br>{item.price}</p>
              <button onClick={() => removeFromCart(item)}>Remove From Cart</button>
              </div>
            ))}
            <h3>Total:</h3>
            {calculateTotal()}
          </div>
        </div>
      </div>
    </div>
    </div>

  );
}

export default MainItems;
