import './App.css';
import { useState } from "react";
import bakeryData from "./assets/tea-data.json";
import TeaItem from './components/TeaItem';
import { Col, Row } from 'antd';

function App() {
  const [cartItems, setCartItems] = useState([])

  function addToCart(item) {
    setCartItems([...cartItems, item])
  }

  function calculateTotal() {
    let total = 0;
    for (let i = 0; i < cartItems.length; i++) {
      total += cartItems[i].price
    }
    return total;
  }

  return (
    <div className="App">
      <h1>My Tea Shop</h1>

      {/* Map tea items from info page to grid */}
      <Row gutter={24}>
        {bakeryData.map((item, index) => (
          <Col lg={4}>
            <div>
              <TeaItem item={item} addToCart={addToCart}></TeaItem>
              <br></br>
            </div>
          </Col>

        ))}
      </Row>

      {/* Render a list of items in the cart */}
      <div>
        <h2>Cart</h2>
        {cartItems.map((item, index) => (<p>{item.name}<br></br>{item.price}</p>))}
        <h3>Total:</h3>
        {calculateTotal()}
      </div>
    </div>
  );
}

export default App;
